<!-- ## 模板题
### [LOJ138](https://loj.ac/p/138)
给定 $n, a, b, c, k_1, k_2$，求
$$
\sum_{x=0}^nx^{k_1}\lfloor \frac{ax+b}{c} \rfloor^{k_2}
$$

简要解：$X$ 为把$x^{k_1}y^{k_2}$加到答案上，并且 `x++`，$Y$ 为把 `y++`。
因为有指数，所以要维护 $x^iy^j$。

```cpp
// Author:  HolyK
// Created: Sun Aug 15 21:59:21 2021
#include <bits/stdc++.h>
template <class T, class U>
inline bool smin(T &x, const U &y) {
  return y < x ? x = y, 1 : 0;
}
template <class T, class U>
inline bool smax(T &x, const U &y) {
  return x < y ? x = y, 1 : 0;
}

using LL = long long;
using PII = std::pair<int, int>;
constexpr int P(1e9 + 7);
inline void inc(int &x, int y) {
  x += y;
  if (x >= P) x -= P;
}
inline void dec(int &x, int y) {
  x -= y;
  if (x < 0) x += P;
}
inline int mod(LL x) {
  return x % P;
}
int fpow(int x, int k = P - 2) {
  int r = 1;
  for (; k; k >>= 1, x = 1LL * x * x % P) {
    if (k & 1) r = 1LL * r * x % P;
  }
  return r;
}
int bin[11][11], n, m;
struct Info {
  int i, x, f[11][11];
  Info() : i(0), x(0) {
    memset(f, 0, sizeof f);
  }
};
Info operator*(Info a, Info b) {
  Info c;
  c.i = (a.i + b.i) % P;
  c.x = (a.x + b.x) % P;
  for (int i = 0; i <= n; i++) for (int j = 0; j <= m; j++) c.f[i][j] = a.f[i][j];
  for (int j = 0, u = 1; j <= n; j++, u = 1LL * u * a.i % P) {
    for (int p = j; p <= n; p++) {
      int bu = 1LL * bin[p][j] * u % P;
      for (int k = 0, v = 1; k <= m; k++, v = 1LL * v * a.x % P) {
        for (int q = k; q <= m; q++) {
          c.f[p][q] = mod(c.f[p][q] + 1LL * bu * bin[q][k] % P * v % P * b.f[p - j][q - k]);
        }
      }
    }
  }
  return c;
}
Info fpow(Info x, LL k) {
  Info r;
  for (; k; k >>= 1, x = x * x) {
    if (k & 1) r = r * x;
  }
  return r;
}
LL div(LL p, LL i, LL r, LL q) {
  return (p * i + r) / q; 
}
Info cal(LL p, LL r, LL q, LL n, const Info &x, const Info &y) {
  if (n < 0) return Info();
  if (r >= q) {
    return fpow(y, r / q) * cal(p, r % q, q, n, x, y);
  }
  if (p >= q) {
    return x * cal(p % q, p % q + r, q, n - 1, fpow(y, p / q) * x, y);
  }
  if (p == 0) {
    return fpow(x, n + 1);
  }
  LL m = div(p, n, r, q);
  return cal(q, q - r + p - 1, p, m - 1, y, x) * fpow(x, n + 1 - (!m ? 0 : div(q, m, p - r - 1, p)));
}
int main() {
  std::ios::sync_with_stdio(false);
  std::cin.tie(nullptr);
  for (int i = 0; i <= 10; i++) {
    bin[i][0] = 1;
    for (int j = 1; j <= i; j++) {
      bin[i][j] = (bin[i - 1][j] + bin[i - 1][j - 1]) % P;
    }
  }
  int t;
  std::cin >> t;
  Info x, y;
  x.i = 1;
  x.f[0][0] = 1;
  y.x = 1;
  while (t--) {
    int n, a, b, c, p, q;
    std::cin >> n >> a >> b >> c >> p >> q;
    ::n = p, ::m = q;
    Info ans = cal(a, b, c, n, x, y);
    std::cout << ans.f[p][q] << "\n";
  }
  return 0;
}
```

### [LOJ6440](https://loj.ac/p/6440)
```cpp
// Author:  HolyK
// Created: Sun Aug 15 15:52:19 2021
#include <bits/stdc++.h>
template <class T, class U>
inline bool smin(T &x, const U &y) {
  return y < x ? x = y, 1 : 0;
}
template <class T, class U>
inline bool smax(T &x, const U &y) {
  return x < y ? x = y, 1 : 0;
}

using LL = long long;
using PII = std::pair<int, int>;
constexpr int P(998244353);
inline void inc(int &x, int y) {
  x += y;
  if (x >= P) x -= P;
}
inline void dec(int &x, int y) {
  x -= y;
  if (x < 0) x += P;
}
inline int mod(LL x) {
  return x % P;
}
int fpow(int x, int k = P - 2) {
  int r = 1;
  for (; k; k >>= 1, x = 1LL * x * x % P) {
    if (k & 1) r = 1LL * r * x % P;
  }
  return r;
}
struct Z {
  int x;
  Z(int v = 0) : x(v < 0 ? v + P : v >= P ? v - P : v) {}
  Z inv() const {
    assert(x);
    return Z(fpow(x));
  }
  Z pow(int k) const {
    return Z(fpow(x, k));
  }
  Z &operator+=(const Z &r) {
    inc(x, r.x);
    return *this;
  }
  Z &operator-=(const Z &r) {
    dec(x, r.x);
    return *this;
  }
  Z &operator*=(const Z &r) {
    x = 1LL * x * r.x % P;
    return *this;
  }
  Z &operator/=(const Z &r) {
    x = 1LL * x * fpow(r.x) % P;
    return *this;
  }
  Z operator+(const Z &r) const {
    return Z(*this) += r;
  }
  Z operator-(const Z &r) const {
    return Z(*this) -= r;
  }
  Z operator*(const Z &r) const {
    return Z(*this) *= r;
  }
  Z operator/(const Z &r) const {
    return Z(*this) /= r;
  }
  Z operator-() const {
    return Z(P - x);
  }
  operator int() const {
    return x;
  }
};

using Matrix = std::vector<std::vector<int>>;
Matrix operator*(const Matrix &a, const Matrix &b) {
  int n = a.size();
  Matrix c(n, std::vector<int>(n));
  for (int k = 0; k < n; k++) {
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < n; j++) {
        c[i][j] = (c[i][j] + 1LL * a[i][k] * b[k][j]) % P;
      }
    }
  }
  return c;
}
Matrix &operator+=(Matrix &a, const Matrix &b) {
  int n = a.size();
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      inc(a[i][j], b[i][j]);
    }
  }
  return a;
}
Matrix operator+(Matrix a, const Matrix &b) {
  return a += b;
}
Matrix fpow(Matrix x, LL k) {
  Matrix r(x.size());
  for (int i = 0; i < r.size(); i++) {
    r[i].resize(r.size());
    r[i][i] = 1;
  }
  for (; k; k >>= 1, x = x * x) {
    if (k & 1) r = r * x;
  }
  return r;
}
struct Info {
  Matrix a, b, ans;
} one;
Info operator*(Info a, Info b) {
  return {
    a.a * b.a,
    a.b * b.b,
    a.ans + a.a * b.ans * a.b
  };
}
Info fpow(Info x, LL k) {
  assert(k >= 0);
  Info r = one;
  for (; k; k >>= 1, x = x * x) {
    if (k & 1) r = r * x;
  }
  return r;
}
LL div(LL p, LL i, LL r, LL q) {
  return ((__int128_t)p * i + r) / q; 
}
Info cal(LL p, LL r, LL q, LL n, const Info &x, const Info &y) {
  if (n < 0) return one;
  if (r >= q) {
    return fpow(y, r / q) * cal(p, r % q, q, n, x, y);
  }
  if (p >= q) {
    return x * cal(p % q, p % q + r, q, n - 1, fpow(y, p / q) * x, y);
  }
  if (p == 0) {
    return fpow(x, n + 1);
  }
  LL m = div(p, n, r, q);
  return cal(q, q - r + p - 1, p, m - 1, y, x) * fpow(x, n + 1 - (!m ? 0 : div(q, m, p - r - 1, p)));
}


int main() {
  std::ios::sync_with_stdio(false);
  std::cin.tie(nullptr);
  LL p, q, r, l, n;
  std::cin >> p >> q >> r >> l >> n;
  Matrix zero(n, std::vector<int>(n)), o = zero;
  for (int i = 0; i < n; i++) {
    o[i][i] = 1;
  }
  one.a = one.b = o, one.ans = zero;
  Matrix a = zero, b = zero;
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      std::cin >> a[i][j];
    }
  }
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      std::cin >> b[i][j];
    }
  }
  auto c = fpow(b, r / q);
  Info x = {a, o, o}, y = {o, b, zero};
  auto ans = cal(p, r, q, l, x, y).ans;

  // std::cerr << b[0][0] << "\n";
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      inc(ans[i][j], P - c[i][j]);
      std::cout << ans[i][j] << " \n"[j + 1 == n];
    }
  }
  return 0;
}
```
